import React, { useState, useRef, useEffect } from "react";
import "../Accordion/accordion.css";

function Accordion({ items }) {
  const [activeIndex, setActiveIndex] = useState(null);
  const [visibleIndexes, setVisibleIndexes] = useState([]);
  const sectionsRef = useRef([]);

  const onTitleClick = (index) => {
    setActiveIndex(index === activeIndex ? null : index);
  };

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          const index = Number(entry.target.dataset.index);
          if (entry.isIntersecting) {
            setVisibleIndexes((prevIndexes) => [...prevIndexes, index]);
          } else {
            setVisibleIndexes((prevIndexes) =>
              prevIndexes.filter((i) => i !== index)
            );
          }
        });
      },
      { threshold: 0.1 }
    );

    sectionsRef.current.forEach((section) => {
      if (section) {
        observer.observe(section);
      }
    });

    return () => {
      if (sectionsRef.current) {
        sectionsRef.current.forEach((section) => {
          if (section) {
            observer.unobserve(section);
          }
        });
      }
    };
  }, []);

  const renderedItems = items.map((item, index) => {
    const isActive = index === activeIndex;
    const isVisible = visibleIndexes.includes(index);
    const contentClassName = `transition max-h-0 overflow-hidden ${
      isActive ? "max-h-screen" : ""
    }`;

    return (
      <div
        key={index}
        className={`section transition-opacity duration-500 ${
          isVisible ? "opacity-100" : "opacity-0"
        }`}
        ref={(el) => (sectionsRef.current[index] = el)}
        data-index={index}
      >
        <button
          onClick={() => onTitleClick(index)}
          className="w-full flex items-center px-4 py-2 bg-white rounded-lg border-black border-2"
        >
          <span className="material-icons px-2">{isActive ? "-" : "+"}</span>
          <span className="freeman-regular">{item.question}</span>
        </button>
        <div className={contentClassName}>
          <p className="px-4 py-2 freeman-regular">{item.answer}</p>
        </div>
      </div>
    );
  });

  return <div className="accordion-wrapper">{renderedItems}</div>; // Add wrapper around rendered items
}

export default Accordion;
