import React, { useState } from "react";
import Slider from "@mui/material/Slider";

const ROICalculator = () => {
  const [students, setStudents] = useState(1000);

  const calculateQuestionsAnswered = (numStudents) => {
    return numStudents * 2.75;
  };

  const handleSliderChange = (event, newValue) => {
    setStudents(newValue);
  };

  return (
    <div className="mx-auto text-center calc">
      <div className="text-4xl alata-regular mb-12 uppercase text-blue-800">
        Ensure the best information is always available to your families{" "}
      </div>
      <div className="flex flex-row">
        <div className="w-full">
          <div className="text-lg text-4xl freeman-regular mb-4">
            Number of Students: {students}
          </div>
          <Slider
            value={students}
            onChange={handleSliderChange}
            aria-labelledby="students-slider"
            min={0}
            max={5000}
            step={100}
            className="mb-4"
            />
          <div className="text-4xl freeman-regular md:mb-16">
            Estimated Questions Answered: {calculateQuestionsAnswered(students)}
          </div>
        </div>
        <div>
          <p className="bubble left alata-regular text-2xl self-center w-full md:w-2/3 mx-auto">
            Save time & energy by providing the information your families need!
          </p>
        </div>
      </div>
    </div>
  );
};

export default ROICalculator;
