import React, { useState } from "react";
import axios from "axios";
import "./register.css";

const Register = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  const handleRegister = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post(
        "http://localhost:5001/api/auth/register",
        {
          email,
          password
        }
      );

      setSuccess("Registration successful!"); // Set success message
      window.location.href = "/login" // Redirect to login page
      setError(null); // Clear any previous errors

      // Optionally, redirect to the login page or login automatically
      // For example:
      // window.location.href = "/login"; // Redirect to login page
    } catch (err) {
      setError(err.response?.data?.errors?.[0]?.msg || "Registration failed");
      setSuccess(null); // Clear any previous success messages
    }
  };

  return (
    <div className="flex text-center register-btn justify-center items-center">
      <div className="alata-regular">
        <h2 className="text-6xl mb-4">Register&nbsp;</h2>
        <form onSubmit={handleRegister}>
          <div className="my-4">
            <label className="text-2xl">Email:&nbsp;</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="my-4">
            <label className="text-2xl">Password:&nbsp;</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <div className="register freeman-regular">
            <button className="rounded-lg p-2 my-4 bg-white text-black uppercase">
              <span>Register</span>
            </button>
          </div>
        </form>
        {error && <p style={{ color: "red" }}>{error}</p>}
        {success && <p style={{ color: "green" }}>{success}</p>}
      </div>
    </div>
  );
};

export default Register;
