import React, { useState } from "react";
import axios from "axios";
import "./login.css";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [token, setToken] = useState(null);
  const [error, setError] = useState(null);

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post(
        "http://localhost:5001/api/auth/login",
        {
          email,
          password
        }
      );

      // Store token in localStorage or state
      setToken(response.data.token);
      localStorage.setItem("token", response.data.token);
      setError(null); // Clear any previous errors

      // Redirect or handle successful login
      // For example:
      window.location.href = "/"; // Redirect to a dashboard or other page
    } catch (err) {
      setError("Invalid Credentials");
    }
  };

  return (
    <div className="flex text-center login-btn justify-center items-center">
      <div className="alata-regular border-black border-2 p-4 rounded-lg">
        <h2 className="text-6xl mb-8">Login</h2>
        <form onSubmit={handleLogin}>
          <div className="my-4">
            <label className="text-2xl">Email:&nbsp;</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="my-4">
            <label className="text-2xl">Password:&nbsp;</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <div className="freeman-regular login">
            <button
              type="submit"
              className="rounded-lg p-2 my-4 bg-white text-black uppercase"
            >
              <span>Login</span>
            </button>
          </div>
        </form>
        {error && <p>{error}</p>}
      </div>
    </div>
  );
};

export default Login;
